import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const HalisahaDetails = () => {
  const { cityName, halisahaName } = useParams();
  const location = useLocation();
  const halisaha = location.state;

  const [reservation, setReservation] = useState({
    date: '',
    time: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setReservation((prev) => ({ ...prev, [name]: value }));
  };

  const handleReservation = () => {
    if (reservation.date && reservation.time) {
      alert(
        `Rezervasyonunuz alındı: 
        Halısaha: ${halisahaName}, 
        Şehir: ${cityName}, 
        Tarih: ${reservation.date}, 
        Saat: ${reservation.time}`
      );
    } else {
      alert('Lütfen tarih ve saat seçiniz.');
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>{halisahaName}</h1>
      <p><strong>Şehir:</strong> {cityName}</p>
      <p><strong>Konum:</strong> {halisaha.location}</p>
      <p><strong>Telefon:</strong> {halisaha.phone}</p>
      <p><strong>Fiyat:</strong> {halisaha.price}</p>

      <h2>Rezervasyon Yap</h2>
      <form style={{ display: 'inline-block', textAlign: 'left' }}>
        <label>
          Tarih:
          <input
            type="date"
            name="date"
            value={reservation.date}
            onChange={handleInputChange}
            style={{ margin: '10px', padding: '5px' }}
          />
        </label>
        <br />
        <label>
          Saat:
          <input
            type="time"
            name="time"
            value={reservation.time}
            onChange={handleInputChange}
            style={{ margin: '10px', padding: '5px' }}
          />
        </label>
        <br />
        <button
          type="button"
          onClick={handleReservation}
          style={{
            marginTop: '10px',
            padding: '10px 20px',
            backgroundColor: '#007BFF',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Rezervasyon Yap
        </button>
      </form>
    </div>
  );
};

export default HalisahaDetails;
