import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const HalisahaDetails = () => {
  const { halisahaName } = useParams();
  const location = useLocation();
  const halisaha = location.state;

  const [reservations, setReservations] = useState([]);
  const [reservation, setReservation] = useState({
    date: '',
    time: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setReservation((prev) => ({ ...prev, [name]: value }));
  };

  const handleReservation = () => {
    const selectedDate = new Date(reservation.date); // Kullanıcının seçtiği tarih
    const today = new Date(); // Bugünün tarihi
    const twoWeeksFromNow = new Date();
    twoWeeksFromNow.setDate(today.getDate() + 14); // 2 hafta sonrası

    // Tarih kontrolü: Bugünden itibaren 14 gün içinde mi?
    if (selectedDate < today || selectedDate > twoWeeksFromNow) {
      alert('Rezervasyonlar yalnızca bugünden itibaren 2 hafta içinde yapılabilir.');
      return;
    }

    if (reservation.date && reservation.time) {
      const newReservation = {
        halisahaName,
        date: reservation.date,
        time: reservation.time,
      };

      setReservations((prev) => [...prev, newReservation]);

      alert(`Rezervasyonunuz alındı: ${reservation.date} - ${reservation.time}`);
    } else {
      alert('Lütfen tarih ve saat seçiniz.');
    }
  };

  if (!halisaha) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h1>Halı Saha Detayları</h1>
        <p>Halı saha bilgisi bulunamadı. Lütfen tekrar deneyiniz.</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>{halisahaName}</h1>
      <p><strong>Konum:</strong> {halisaha.location}</p>
      <p><strong>Telefon:</strong> {halisaha.phone}</p>
      <p><strong>Fiyat:</strong> {halisaha.price}</p>

      <h2>Rezervasyon Yap</h2>
      <form style={{ display: 'inline-block', textAlign: 'left' }}>
        <label>
          Tarih:
          <input
            type="date"
            name="date"
            value={reservation.date}
            onChange={handleInputChange}
            style={{ margin: '10px', padding: '5px' }}
          />
        </label>
        <br />
        <label>
          Saat:
          <input
            type="time"
            name="time"
            value={reservation.time}
            onChange={handleInputChange}
            style={{ margin: '10px', padding: '5px' }}
          />
        </label>
        <br />
        <button
          type="button"
          onClick={handleReservation}
          style={{
            marginTop: '10px',
            padding: '10px 20px',
            backgroundColor: '#007BFF',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Rezervasyon Yap
        </button>
      </form>

      <h2>Rezervasyonlar</h2>
      <ul>
        {reservations.map((res, index) => (
          <li key={index}>
            {res.date} - {res.time}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HalisahaDetails;
