import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const CityHalisahaList = () => {
  const { cityName } = useParams(); 
  const navigate = useNavigate();

  // Halı saha verisi (Her halı saha için daha fazla bilgi ekledik)
  const halisahaData = {
    Mersin: [
      { name: 'Vadi Halısaha', location: 'Merkez', phone: '555-1234', price: '1960 TL/saat' },
      { name: 'Adonis Halısaha', location: 'Merkez', phone: '555-5678', price: '1960 TL/saat' },
      { name: 'Karaca Halısaha', location: 'Merkez', phone: '5', price: '1960 TL/saat' },
    ],
    Adana: [
      { name: 'Park Arena Halısaha', location: 'Seyhan', phone: '541 987 21 54', price: '1960 TL/saat' },
      { name: 'Olimpos Halısaha', location: 'Çukurova', phone: '533 856 45 87', price: '1960 TL/saat' },
      { name: 'Adana Halısaha 3', location: 'Yüreğir', phone: '555 852 19 03', price: '1960 TL/saat' },
    ],
  };

  const halisahalar = halisahaData[cityName] || [];

  const handleHalisahaSelect = (halisaha) => {
    navigate(`/city/${cityName}/halisaha/${halisaha.name}`, { state: halisaha });
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>{cityName} Halı Sahaları</h1>
      {halisahalar.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {halisahalar.map((halisaha, index) => (
            <li key={index} style={{ margin: '10px 0' }}>
              <button
                onClick={() => handleHalisahaSelect(halisaha)}
                style={{
                  padding: '10px 20px',
                  fontSize: '16px',
                  cursor: 'pointer',
                }}
              >
                {halisaha.name}
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Bu şehirde halı saha bulunamadı.</p>
      )}
    </div>
  );
};

export default CityHalisahaList;
