import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import Wrapper from './Wrapper';

const HalisahaDetails = () => {
  const { halisahaName } = useParams();
  const location = useLocation();
  const halisaha = location.state;

  const [reservations, setReservations] = useState([]);
  const [reservation, setReservation] = useState({ day: '', time: '' });

  if (!halisaha) {
    return (
      <Wrapper>
        <h1 className="text-3xl font-bold text-center text-red-600">Halı Saha Detayları</h1>
        <p className="text-center">Halı saha bilgisi bulunamadı. Lütfen tekrar deneyiniz.</p>
      </Wrapper>
    );
  }

  return (
    <Wrapper>
      <h1 className="text-3xl font-bold mb-4">{halisahaName}</h1>
      <p><strong>Konum:</strong> {halisaha.location}</p>
      <p><strong>Telefon:</strong> {halisaha.phone}</p>
      <p><strong>Fiyat:</strong> {halisaha.price}</p>

      <h2 className="text-xl font-semibold mt-6">Rezervasyon Yap</h2>
      <form className="space-y-4">
        {/* Gün ve Saat Seçimi */}
      </form>

      <h2 className="text-xl font-semibold mt-6">Rezervasyonlar</h2>
      <ul className="space-y-2">
        {reservations.map((res, index) => (
          <li key={index} className="bg-gray-100 text-black p-2 rounded-lg">
            <span className="font-bold">{res.day}</span> - {res.time}
          </li>
        ))}
      </ul>
    </Wrapper>
  );
};

export default HalisahaDetails;
